import{_ as e,c as t}from"./app.921374b6.js";const n={},o=t('<h1 id="exploring-the-user-interface" tabindex="-1"><a class="header-anchor" href="#exploring-the-user-interface" aria-hidden="true">#</a> Exploring the User Interface</h1><h2 id="ims-explorer-views-and-controls" tabindex="-1"><a class="header-anchor" href="#ims-explorer-views-and-controls" aria-hidden="true">#</a> IMS Explorer Views and Controls</h2><h3 id="ims-connections-tree-view" tabindex="-1"><a class="header-anchor" href="#ims-connections-tree-view" aria-hidden="true">#</a> IMS Connections Tree View</h3><ul><li><strong>Purpose:</strong> Add and manage multiple IMS database and catalog connections.</li><li><strong>Usage:</strong><ul><li>Add and organize connections.</li><li>Expand/collapse connection contents to browse <strong>DBDs</strong>, <strong>PSBs</strong>, and <strong>PCBs</strong>.</li><li>Right\u2011click an item to view <strong>DDL</strong>, <strong>segments</strong>, <strong>fields</strong>, and <strong>source</strong>.</li></ul></li></ul><h3 id="ims-projects-view" tabindex="-1"><a class="header-anchor" href="#ims-projects-view" aria-hidden="true">#</a> IMS Projects View</h3><ul><li><strong>Purpose:</strong> Create and edit IMS projects that contain SQL and related assets.</li><li><strong>Usage:</strong> Start new projects, add SQL query files, organize SQL code, and keep connection metadata near your code.</li></ul><h3 id="table-views" tabindex="-1"><a class="header-anchor" href="#table-views" aria-hidden="true">#</a> Table Views</h3><ul><li><strong>Purpose:</strong> Inspect DBDs, PSBs, and PCBs in tabular form.</li><li><strong>Usage:</strong> Quickly click across related definitions to find associated segments and fields.</li></ul><h3 id="output-channels" tabindex="-1"><a class="header-anchor" href="#output-channels" aria-hidden="true">#</a> Output Channels</h3><ul><li><strong>IBM IMS Explorer for VS Code</strong> \u2014 Client logs</li><li><strong>IBM IMS Explorer LSP Server</strong> \u2014 Language server logs<br> View them via the <strong>Output</strong> panel to troubleshoot or monitor activity.</li></ul><h3 id="status-bar-options" tabindex="-1"><a class="header-anchor" href="#status-bar-options" aria-hidden="true">#</a> Status Bar Options</h3><ul><li><strong>IMS Workspace</strong> \u2014 Indicates the workspace location that stores extension artifacts such as <code>ims.connections.json</code>. Can be clicked to choose a location.</li><li><strong>IMS Connection</strong> \u2014 Used to verify or switch the active database connection for SQL execution.</li></ul><h3 id="settings-integration" tabindex="-1"><a class="header-anchor" href="#settings-integration" aria-hidden="true">#</a> Settings Integration</h3><p>While not indicated on the UI, these extension settings are key to using the IMS Explorer for VS Code extension effectively.</p><ul><li><strong>Java selection</strong> (<code>ims.explorer.connection.property.javaHome</code>) and <strong>memory</strong> (<code>ims.explorer.connection.property.maxHeapSize</code>).</li><li><strong>Logging level</strong> (<code>ims.explorer.logging.level</code>).</li><li><strong>IMS Workspace path</strong> (<code>ims.explorer.property.ImsWorkspace</code>). Configure under <strong>Preferences \u2192 Settings</strong>.</li></ul>',15);function r(s,i){return o}var l=e(n,[["render",r],["__file","Exploring-the-User-Interface.html.vue"]]);export{l as default};
